import { View, Text, StyleSheet, Image, TextInput } from "react-native";
import React from "react";

export default function Login() {
  return (
    <View style={styles.loginPage}>
      <View style={styles.loginView}>
        <Image
          style={styles.image}
          source={require("../../../assets/intro.png")}
        />
        {/* <Text style={styles.text}>Welcome to Tea Cup</Text> */}
      </View>
      <View style={styles.loginCred}>
        <View style={styles.loginCred}>
          <View style={styles.formInput}>
            <Text>username</Text>
            <TextInput
              style={styles.input}
              placeholder="Username"
              // Add other TextInput props as needed
            />
          </View>
          <View>
            <Text>password</Text>
            <TextInput
              style={styles.input}
              placeholder="Password"
              secureTextEntry
              // Add other TextInput props as needed
            />
          </View>
        </View>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  loginView: {
    padding: 10,
    width: "100%",
    height: "50%",
    flex: 1,
    flexDirection: "column",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
  text: {
    fontSize: 20,
    fontWeight: "bold",
    color: "white",
  },
  image: {
    width: "100%",
    height: "100%",
  },
  loginPage: {
    flex: 1,
    // backgroundColor: "#597C64",
    width: "100%",
    height: "100%",
    justifyContent: "center",
    alignContent: "center",
  },
  loginCred: {
    backgroundColor: "#597C64",
    width: "100%",
    height: "50%",
    flex: 1,
    justifyContent: "center",
    alignItems: "start",
    padding: 10,
  },
  formInput: {
    flex: 1,
    width: "100%",
    height: "100px",
    justifyContent: "center",
    // gap: 2,
    flexDirection: "row",
  },
});
